@use '@/assets/scss/main.scss' as *;

.drag-icon {
  cursor: grab; /* Изменение курсора */
  font-size: 18px; /* Размер иконки */
  color: #333; /* Цвет иконки */
}

.dashed-line {
  width: 45%;
  height: 1px;
  margin: 10px 10px; /* Отступы */
}

.left {
  background: linear-gradient(to right, #fff 0%, #fff 50%, #7e7e7e 50%);
  background-size: 45px 1px;
  color: #ACB2CA;
  float: left; /* Выравнивание по левому краю */
}

.right {
  background: linear-gradient(to left, #fff 0%, #fff 50%, #7e7e7e 50%);
  background-size: 45px 1px;
  color: #ACB2CA;
  float: right; /* Выравнивание по правому краю */
}

.main-content {
  gap: 0 !important;
  border-radius: 20px;
  padding: 20px;
  @include theme('background', $general-background-light);
}

.p-multiselect {
  padding: 25px 20px;
  border-radius: 15px;
  margin: 10px 0;
}

.participants-container {
  margin-top: 20px;
  display: flex;
  gap: 20px;
}

.search-filter-form {
  width: 30%;
  display: flex;
  gap: 20px;
}

.search-bar input {
  width: 200px;
}

.filter-bar .p-dropdown {
  width: 200px;
}

.p-button {
  padding: 0.5rem;
}

.p-multiselect-filter-container {
  border: 2px solid red !important;
}

.checkbox {
  margin: 10px 0;
  // Увеличение размера чекбокса
  input[type="checkbox"] {
    transform: scale(3); // Увеличиваем размер в 1.5 раза
  }
}

/* Стили для секций и материалов */
.sections {
  display: flex;
  flex-direction: column;
  gap: 20px; /* Расстояние между секциями */
}

.section-wrapper {
  border: 1px solid #e2e8f0; /* Цвет и стиль обводки секции */
  border-radius: 8px; /* Закругление углов обводки */
  padding: 10px; /* Отступ внутри обводки */
  cursor: pointer; /* Изменение курсора при наведении */
}

.section {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 500px;
}

.sections .section-title-input {
  width: 60%;
  border-radius: 5px;
  border: 2px solid black;
  background: transparent;
  font-weight: inherit;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
}

.section-content {
  margin-top: 10px;

  .content-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .content1 {
    padding-bottom: 10px;
    border-bottom: 1px solid gray;
    margin-bottom: 10px;
  }
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
}

.modal-content {
  background-color: #fff;
  padding: 20px;
  border-radius: 8px;
}

.material-types {
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
}

.file-container {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 10px;
}

.file-link {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-decoration: none;
  color: inherit;
  position: relative;
}

.file-icon-wrapper {
  font-size: 46px; /* Размер иконки */
}

.file-icon {
  font-size: inherit; /* Наследование размера шрифта */
}

.file-name {
  //text-align: center;
  //overflow: hidden;
  //text-overflow: ellipsis; /* Обрезание длинных имен файлов */
  //white-space: nowrap;
  width: 100px; /* can be 100% ellipsis will happen when contents exceed it */
  display: inline-block;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;

  //&:hover {
  //
  //  white-space: normal;
  //  width: auto;
  //}
}

.file-link:hover .file-name::after {
  white-space: normal;
  width: auto;
  content: attr(data-full-name); /* Отображение полного имени файла при наведении */
  position: absolute;
  background-color: white;
  padding: 5px;
  border: 1px solid #ccc;
  border-radius: 5px;
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
  z-index: 1;
}
