transforms.result:
@use '@/assets/scss/main.scss' as *;

ul {
  list-style-type: none;
}

.slide-enter-active,
.slide-leave-active {
  transition-property: all;
  transition-duration: .1s;
  transition-timing-function: ease-out;
}

.slide-enter-from,
.slide-leave-to {
  opacity: 0;
  transform: translateX(-50%);
}

.navigation {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  gap: 20px;
  transition: all .3s ease;
}

.navigation-dropdown {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  gap: 10px;
  position: absolute;
  height: 100vh;
  width: max-content;
  padding: 10px 10px 40px 10px;
  border-radius: 0 10px;
  z-index: 99;
  top: 0;
  left: 0;
  @include theme('box-shadow', $shadow-color, true, 2px 0px 8px 0px);
  @include theme('background', $general-background-light);
}

.logo-img {
  height: 50px;
}

.navigation-main, .navigation-other {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  width: max-content;
  gap: 5px;
  padding: 0;
}

.navigation-other {
  margin-top: auto;
}

.nav-link {
  text-align: center;
  list-style-type: none;
  border-radius: 5px;
  width: 100%;
  transition: all 0.3s ease;

  a {
    padding: 5px;
    display: flex;
    gap: 10px;
    align-items: center;
    justify-content: flex-start;
    @include theme('color', $primary-text-color);
  }

  & i {
    margin: 5px;
  }

  &:hover {
    @include theme('background-color', $primary-color);

    & i {
      color: white;
    }
  }

  & :hover span {
    @include theme('color', $primary-color-text);
  }
}

.nav-link:hover .icon-home,
.nav-link:hover .icon-profile,
.nav-link:hover .icon-logout,
.nav-link:hover .icon-book,
.nav-link:hover .icon-book_add,
.nav-link:hover .icon-book_change,
.nav-link:hover .icon-admin_panel {
  @include theme('background', $primary-color-text);
}

.text-nav {
  user-select: none;
  font-size: 20px;
  @include theme('color', $primary-text-color);
}

.menu-open {
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 25px;
  height: 25px;
  right: -18px;
  top: 25px;
  padding: 10px;
  border-radius: 50%;
  transform: rotate(-90deg);
  transition: all 0.3s ease;
  @include theme('background-color', $divider-color, false, !important);

  .icon-arrow {
    margin: 0px;
    position: absolute;
  }
}

.header-logo-text {
  display: flex;
  flex-direction: column;
  justify-content: start;
  user-select: none;
  z-index: 0;
  @include theme('color', $primary-color);

  & .name {
    font-size: 28px;
  }

  & .profession {
    font-size: 16px;
  }
}

.notifications-list {
  list-style-type: none;
  padding: 0;
  margin: 0;
  padding-right:5px;
  max-height: 300px; /* Установите максимальную высоту, при которой будет появляться скроллер */
  overflow-y: auto;
  & li {
    margin-bottom: 10px;
    border-radius: 5px;
    border: 1px solid #bbbbbb;
  }
}

.notification-content {
  padding: 10px;
}

.notification-dot {
  width: 8px;
  height: 8px;
  background-color: limegreen; /* Цвет точки можете настроить на свой вкус */
  border-radius: 50%;
  margin-left: auto;
  margin-right: 10px;
}

.unread .notification-content {
  font-weight: bold; /* Пример стилизации непрочитанных уведомлений */
}

.badge {
  position: absolute;
  left: 15px;
  top:-5px;
}
